For example: wxDEFINE_EVENT(MY_COMMAND_EVENT,wxCommandEvent);

classMyCustomEvent:publicwxEvent{...};
wxDEFINE_EVENT(MY_CUSTOM_EVENT,MyCustomEvent);

For example: wxDECLARE_EVENT(MY_COMMAND_EVENT,wxCommandEvent);

classMyCustomEvent:publicwxEvent{...};
wxDECLARE_EVENT(MY_CUSTOM_EVENT,MyCustomEvent);

Example of use: classMyEvent:publicwxEvent{...};

//notethatthisisnotnecessaryunlessusingoldcompilers:forthe
//reasonablynewonesjustuse&funcinsteadofMyEventHandler(func)
typedefvoid(wxEvtHandler::*MyEventFunction)(MyEvent&);
#defineMyEventHandler(func)wxEVENT_HANDLER_CAST(MyEventFunction,func)

wxDEFINE_EVENT(MY_EVENT_TYPE,MyEvent);

#defineEVT_MY(id,func)\
wx__DECLARE_EVT1(MY_EVENT_TYPE,id,MyEventHandler(func))

...

wxBEGIN_EVENT_TABLE(MyFrame,wxFrame)
EVT_MY(wxID_ANY,MyFrame::OnMyEvent)
wxEND_EVENT_TABLE()
